mapper.select:
SELECT t1.id,/** 产品ID **/ t1.`code`,/** 产品代码 **/ t1.`name`,/** 产品名称 **/ concat_ws( '-', t1.`code`, t1.`name` ) AS sku_code_name, t1.price,/** 产品价格 **/ t1.create_user,/** 产品创建人 **/ t1.create_time,/** 产品创建时间 **/ t1.update_user,/** 产品修改人 **/ t1.update_time,/** 产品修改时间 **/ t1.del_tag,/** 产品删除标记 **/ t1.remark, /** 产品备注 **/ t2.id spu_id,/** 货品ID **/ t2.`code` spu_code,/** 货品编码 **/ t2.`name` spu_name,/** 货品名称 **/ concat_ws( '-', t2.`code`, t2.`name` ) AS spu_code_name, t3.id brand_id,/** 品牌ID **/ t3.`code` brand_code,/** 品牌编码 **/ t3.`name` brand_name,/** 品牌名称 **/ concat_ws( '-', t3.`code`, t3.`name` ) AS brand_code_name, t4.id spu_type_id,/** 货品类型ID **/ t4.`code` spu_type_code,/** 货品类型编码 **/ t4.`name` spu_type_name,/** 货品类型名称 **/ concat_ws( '-', t4.`code`, t4.`name` ) AS spu_type_code_name, t7.id spe_id,/** 产品规格ID **/ t7.`name` spe_name, /** 产品规格名称 **/ concat_ws( '-', t7.id, t7.`name` ) AS spe_id_name FROM tbl_sku t1 /** 产品表 **/ LEFT JOIN tbl_spu t2 ON t2.id=t1.spu_id /** 货品表 **/ LEFT JOIN tbl_spu_brand t3 ON t3.id=t2.brand_id /** 品牌表 **/ LEFT JOIN tbl_spu_type t4 ON t2.id=t4.id /** 货品类型表 **/ LEFT JOIN tbl_sku_specification_option t5 ON t1.id=t5.sku_id /** 产品规格选项表 **/ LEFT JOIN tbl_specification_option t6 ON t5.spec_opti_id=t6.id /** 规格选项表 **/ LEFT JOIN tbl_specification t7 ON t6.spec_id=t7.id /** 规格表 **/
and t1.id = #{id,jdbcType=VARCHAR}
and t1.`name` like concat('%', #{name,jdbcType=VARCHAR}, '%')
and t1.del_tag = #{delTag,jdbcType=VARCHAR}
