mapper.select:
select t1.id sku_id,t1.`code` sku_code,t1.`name` sku_name,t4.id id,t4.`name` spec_name, t3.id spec_opti_id,t3.`value` spec_opti_value,t2.create_user,t2.create_time,t2.update_user, t2.update_time,t2.del_tag,t2.remark FROM tbl_sku t1 LEFT JOIN tbl_sku_specification_option t2 ON t1.id=t2.sku_id LEFT JOIN tbl_specification_option t3 ON t2.spec_opti_id=t3.id LEFT JOIN tbl_specification t4 ON t3.spec_id=t4.id
and t1.id = #{id,jdbcType=VARCHAR}
and t1.code = #{code,jdbcType=VARCHAR}
and t1.`name` like concat('%', #{name,jdbcType=VARCHAR}, '%')
and t1.del_tag = #{delTag,jdbcType=VARCHAR}
