mapper.select:
select 

from tbl_role_user_relation
where id = #{id,jdbcType=VARCHAR}

  and del_tag = #{delTag,jdbcType=VARCHAR}

select



FROM tbl_role t1
LEFT JOIN tbl_role_user_relation t2
ON t1.id = t2.role_id AND t1.del_tag = #{delTag,jdbcType=VARCHAR} and t2.del_tag = #{delTag,jdbcType=VARCHAR}
LEFT JOIN tbl_user t3
ON t2.user_id = t3.id AND t3.del_tag = #{delTag,jdbcType=VARCHAR}
WHERE t3.id = #{userId,jdbcType=VARCHAR}
